const modalObject = ({member, thingInfo, handleClose, handleChange}) => {
    return (
        <dialog id="modal-thing" open>
            <article>
                <a href="#" aria-label="Close" className="close" data-target="modal-thing" onClick={handleClose}>Close</a>
            <hgroup>
                    <h3>{member.name}, ID #{member.id} in a modal</h3>
                    <p>{member.bio}</p>
            </hgroup>
            <hgroup>
                    <a style={{ color: "red" }} href="#" role="button" aria-label="Next" className="outline" data-target="modal-thing" onClick={() => {
                        handleChange(Number(member.id) + 1)
                    }}>Next </a>
                    <a style={{ color: "green" }} href="#" role="button" aria-label="Next" className="outline" data-target="modal-thing" onClick={() => {
                        handleChange(Number(member.id) - 1)
                    }}>Previous</a>
            </hgroup>
            </article>
        </dialog>
    )
}

export default modalObject;
