import { useRef } from 'react';
const modalObject = ({ member, thingInfo, handleClose, handleChange }) => {
    
    const [cast, setCast] = [,]; /* Setting a constant for holding data. useState will generate the state */

    async function fetchStuff() {        // Async function expects something to be returned. 
        const response = await fetch('cast.json') // Can only use Await in an async function. Pauses function until promosed thing is resolved
        setCast(await response.json());
    }

    const initialized = useRef(false);  //Determines if initialized

    useEffect(() => {
        if (!initialized.current) { //If intialised is currently false fetches cast.json file. 
            initialized.current = true //Sets the constant to true to avoid fetching the data again. 
            fetchStuff();
        }
    }, [])

    return (
        <dialog id="modal-thing" open>
            <article>
                <a href="#pika" aria-label="Close" className="close" data-target="modal-thing" onClick={handleClose}>Close</a>
            <hgroup>
                    <h3>{member.name}, ID #{member.id} in a modal</h3>
                    <p id="pika">{member.bio}</p>
            </hgroup>
            <hgroup>
                    <a style={{ color: "red" }} href="#pika" role="button" aria-label="Next" className="outline" onClick={() => {
                        handleChange(Number(member.id) + 1)
                    }}>Next </a>
                    <a style={{ color: "green" }} href="#pika" role="button" aria-label="Previous" className="outline"  onClick={() => {
                        handleChange(Number(member.id) - 1)
                    }}>Previous</a>
            </hgroup>
            </article>
        </dialog>
    )
}

export default modalObject;
